"use strict";
var num = 2;
console.log(num); // Output: 2
let str = "Hello World!";
// Check if the variable is a number
if (typeof num === 'number') {
    console.log(num + 1); // Output: 3
}
// Check if the variable is a string    
if (typeof str === 'string') {
    console.log(str.length); // Output: 13
}
